#include <iostream>#include "LinkedList.h"#include <time.h>#include <cstdlib>using namespace std;struct MyStruct {	int num;	float amount;	bool operator ==( const MyStruct & right ) { return num == right.num; }	inline int operator +( ) { return num; }};int main( ){	srand( unsigned int( time( NULL ) ) );	MyStruct m;	LinkedList<MyStruct> ll;	for ( int i = 0; i < 20; i++ ) {		m.num = (rand( ) % 5) + 10;		m.amount = float( rand() % 10000 );		ll.insert( m );	}		cout << "Records in unsorted linked list:" << endl;	ll.first( m );	do {		cout << m.num << "     " << m.amount << endl;	} while ( ll.getNext( m ) );	ll.countingSort( );	cout << "Records in sorted linked list:" << endl;	ll.first( m );	do {		cout << m.num << "     " << m.amount << endl;	} while ( ll.getNext( m ) );	return 0;}