// checkbook.h ö a class template for a Checkbook, where the check is any data type// to use a struct for the DataType, you must overload the following operators://	>	left operand:  struct object	right operand:  float//		used to compare the amount of the check in the struct object with the //		balance//	-=	left operand:  float		right operand:  struct object//		used to subtract the amount of the check in the struct object from the//		balancetemplate <class DataType>class Checkbook{public:	void setBalance( float amount );	bool writeCheck( DataType amount );  // returns false if amount is greater than balance; 										 // otherwise returns true	void deposit( float amount );	float getBalance(  );	DataType getLastCheck( );	float getLastDeposit( ); private:	float balance;	DataType lastCheck;	float lastDeposit;};#include "checkbook.cpp"