#include <iostream>#include <iomanip>#include "checkbook.h"using namespace std;int main( ){	float bal;	cout << "Enter your initial balance: ";	cin >> bal;	Checkbook<float> cbook( bal );		cbook.deposit( 500.00 );	cbook.writeCheck( 25.00 );		cout << fixed << showpoint << setprecision( 2 );	cout << "The balance is now: $" << cbook.getBalance( ) << endl;	return 0;}