// MinBalCheckBook.cpp  -- The function definitions of the class template for MinBalCheckBooktemplate <class DataType>MinBalCheckBook<DataType>::MinBalCheckBook( float initBalance, float minBal, float sFee, float cFee ): CheckBook<DataType>( initBalance ), minBalance( minBal ), serviceFee( sFee ), checkFee( cFee ){}// returns false if amount is greater than balance; otherwise returns truetemplate <class DataType>bool MinBalCheckBook<DataType>::writeCheck( const DataType & amount ){	bool success = CheckBook<DataType>::writeCheck( amount );	if ( success && balance < minBalance )		balance -= checkFee;	return success;}template <class DataType>void MinBalCheckBook<DataType>::deductServiceFee( ){	if ( balance < minBalance )		balance -= serviceFee;}