// queue.h -- class template for the Array implementation of a Queue// The constructor, the (default) copy constructor, the (default) assignment operator,// and the enqueue function can throw exceptions if there is no more heap memory.#include "Array.h"template <class DataType>class Queue{public:  	Queue( );	void enqueue( DataType element );	// dequeues element and returns it in deqElement	// returns false if called on an empty queue; otherwise returns true	bool dequeue( DataType & deqElement );  	// returns the front element in frontElement without removing it	// returns false if called on an empty queue; otherwise returns true	bool peek( DataType & frontElement );	bool isEmpty( ) const;		// returns true if queue is empty					// otherwise returns false	void makeEmpty( );private:	Array<DataType> elements;	int front;	int back;};#include "queue.cpp"