template <class T> class MyClass{public:   MyClass();   MyClass(T initialData);   void setData(T newData);   T getData();private:   T theData;};template <class T> MyClass<T>::MyClass() {}template <class T> MyClass<T>::MyClass(T initialData)                : theData(initialData) {}template <class T> void MyClass<T>::setData(T newData){	theData = newData;}template <class T> T MyClass<T>::getData(){	return theData;}