#include <iostream>using namespace std;struct Node{  char  item;   Node *next;};  // end structNode *stringPtr;void writeString(Node *stringPtr)// ---------------------------------------------------------// Writes a string.// Precondition: The string is represented as a linked list// to which the pointer stringPtr points.// Postcondition: The string is displayed. The linked list// and stringPtr are unchanged.// ---------------------------------------------------------{   if (stringPtr != NULL)   {  // write the first character      cout << stringPtr->item;      // write the string minus its first character      writeString(stringPtr->next);   }  // end if}  // end writeString